mapper.update:
update pms_sku
set stock_locked = stock_locked + #{number}
where id = #{skuId}
  and stock >= stock_locked + #{number}

mapper.select:
select psk.id, psk.id skuId, psk.code skuCode, psk.name skuName, psk.pic_url skuPic, psk.origin_price skuOriginPrice, psk.price skuPrice, psk.stock stock, psp.id spuId, psp.name spuName, psp.pic_url spuPic, pc.id categoryId, pc.name categoryName, pb.id brandId, pb.name brandName from pms_sku psk left join pms_spu psp on psk.spu_id = psp.id left join pms_category pc on psp.category_id = pc.id left join pms_brand pb on psp.brand_id = pb.id
psk.id in
#{skuId}
